import React, { Suspense, lazy, Component} from 'react';
import '../css/da-objects.css';

export default class Collection extends Component {

  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false,
      collection: []
    };

  }

  //API 호출
  componentDidMount() {
    fetch("/dummy/Collection.json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            collection: result
          });

        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );

  }

  render() {

    const { error, isLoaded, collection } = this.state;

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading.........</div>;
    } else {

      var ItemType = null;

      return (
        <div className="container daCol">

          {collection.map(function(item, i){
            ItemType = React.lazy(() => import('../components/daMdl/'+item.type+'/'+item.name+'.js'));
            return (
              <Suspense fallback={<div>Loading...</div>}>
                <ItemType data={item.props}/>
              </Suspense>
            );
           })}

        </div>
      );

    }
  }
}
