import React, { Suspense, lazy, Component} from 'react';
import '../css/da-objects.css';

export default class Collection extends Component {

  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false,
      header: null,
      items:[],
      collection: []
    };

  }

  //API 호출
  componentDidMount() {
    fetch("http://aa.durumi.io/aa/items/"+this.props.item)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            header: React.lazy(() => import('../components/daMdl/Tblog/Mheader.js')),
            double: React.lazy(() => import('../components/daMdl/Tblog/MdoubleCrop.js')),
            items: result.data
          });

        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );

    fetch("/dummy/Collection.json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            collection: result
          });

        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );

  }

  render() {

    const { error, isLoaded, items, collection } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {

      console.log(collection);
      const Mheader = this.state.header;
      const MdoubleCrop = this.state.double;
      return (
        <div className="container daCol">
          <Suspense fallback={<div>Loading...</div>}>
            <Mheader/>
          </Suspense>
          <Suspense fallback={<div>Loading...</div>}>
            <MdoubleCrop/>
          </Suspense>




          {items.map(item => (

          <div className="card mb-3" key={item.id.toString()}>
            <div className="row no-gutters">
              <div className="col-md-4">
                <img src="http://aa.durumi.io/aa/assets/fotx582wmqokk44s?key=directus-small-crop" className="card-img" alt="..."/>
              </div>
              <div className="col-md-8">
                <div className="card-body">
                  <h5 className="card-title">{item.title}</h5>
                  <p className="card-text">{item.description}</p>
                  <p className="card-text"><small className="text-muted">{item.created_on}</small></p>
                </div>
              </div>
            </div>
          </div>

          ))}
        </div>
      );

    }
  }
}
