      return (
        <div className="container daCol">

          {collection.map(function(item, i){
            ItemType = React.lazy(() => import('../components/daMdl/'+item.type+'/'+item.name+'.js'));
            return (
              <Suspense fallback={<div>Loading...</div>}>
                <ItemType />
              </Suspense>
              );
           })}


