import React, {Component} from 'react';

export default class MsingleCrop extends Component {

  constructor(props) {
    super(props);

    this.state = {
      title: "2020 서울 관광 페스티벌",
      content: "본문 내용이 여기 들어갑니다. 반갑습니다.본문 내용이 여기 들어갑니다. ",
      image: "https://image.shutterstock.com/image-photo/planet-earth-eastern-hemisphere-this-600w-589176830.jpg"
    }
  }
  render() {
    return (
      <div className="daMdl Tblog MsingleCrop">         
        <div className="thumb"><img src={this.state.image} alt={this.state.title} /></div>
        <div className="conA">
          <div className="inner">
            <dl className="meta">
              <dt>{this.state.title}</dt>
              <dd>{this.state.content}</dd>
            </dl>
          </div>
        </div>
      </div>
    );
  }
}
